mapper.select:
SELECT * FROM session
WHERE conference_id=${conference_id}
AND ((visible=1 AND conference_visible=1) OR establisher_id=${user_id})

mapper.insert:
INSERT INTO conference

    organization, introduction, date, chairs, place, start_time, end_time, tags, visible


    #{organization}, #{introduction}, #{date}, #{chairs}, #{place}, #{start_time}, #{end_time}, #{tags}, ${visible}
;
